import { gql } from '@apollo/client';


const REGISTER_USER = gql`
  mutation register(
    $username: String!
    $email: String!
    $password: String!
    $confirmPassword: String!
  ) {
    register(
      registerInput: {
        username: $username
        email: $email
        password: $password
        confirmPassword: $confirmPassword
      }
    ) {
      id
      email
      username
      createdAt
      token
    }
  }
`;

const ADD_TODO = gql`
  mutation addTodo($title: String, $detail: String, $date: Date) {
    addTodo(title: $title, detail: $detail, date: $date) {
      id
      title
      detail
      date
      username
    }
  }
`;

const DELETE_TODO = gql`
  mutation deleteTodo($id: ID) {
    deleteTodo(id: $id)
  }
`;

export {
  REGISTER_USER,
  ADD_TODO,
  DELETE_TODO,
  
}
